styles:
.sh-context--container{
  font-family: sans-serif;
  position: fixed;
  background: #ececec;
  min-width: 150px;
  border: 1px solid rgba(0,0,0,0.2);
  border-radius: 3px;
  box-shadow: 0 0 10px 2px rgba(0,0,0,0.1);
  z-index: 100;
  color: black;
}

.sh-context--container ul{
  list-style: none;
  padding: 5px 0;
  margin: 0;
}

.sh-context--container ul li{
    padding: 5px 10px 5px 15px;
    transition: all 0.15s;
}

.sh-context--container ul li.sh-context-divider{
    height: 1px;
    margin: 1px 1px 8px 1px;
    overflow: hidden;
    background-color: #ececec;
    border-bottom: 1px solid #d0d0d0;
    line-height: 10px;
  }

.sh-context--container ul li.sh-menu-item:hover{
    cursor: pointer;
    background: #4b8bec;
    color: white;
}

.sh-context--container ul li.sh-menu-disabled{
    color: #d0d0d0;
 }

 .sh-context--container ul li.sh-menu-item.sh-menu-hidden{
    display: none;
 }

.sh-context--container ul li.sh-menu-disabled:hover{
    cursor: not-allowed;
    color: #d0d0d0;
    background: #ececec;
}

.right-arrow{
  float: right;
  margin-left: 10px;
  margin-top: 3px;
  border-top: 6px solid transparent;
  border-bottom: 6px solid transparent;
  border-left: 8px solid black;
}

.left-arrow{
